changeSet.sql:
-- Enable RLS on all multi-tenant tables
ALTER TABLE eco_product ENABLE ROW LEVEL SECURITY;
ALTER TABLE eco_supplier ENABLE ROW LEVEL SECURITY;
ALTER TABLE eco_importer ENABLE ROW LEVEL SECURITY;
ALTER TABLE installation ENABLE ROW LEVEL SECURITY;
ALTER TABLE emission_record ENABLE ROW LEVEL SECURITY;
ALTER TABLE emission_report ENABLE ROW LEVEL SECURITY;
ALTER TABLE compliance_report ENABLE ROW LEVEL SECURITY;

changeSet.rollback:
ALTER TABLE eco_product DISABLE ROW LEVEL SECURITY;
ALTER TABLE eco_supplier DISABLE ROW LEVEL SECURITY;
ALTER TABLE eco_importer DISABLE ROW LEVEL SECURITY;
ALTER TABLE installation DISABLE ROW LEVEL SECURITY;
ALTER TABLE emission_record DISABLE ROW LEVEL SECURITY;
ALTER TABLE emission_report DISABLE ROW LEVEL SECURITY;
ALTER TABLE compliance_report DISABLE ROW LEVEL SECURITY;

changeSet.sql:
-- Product RLS policy CREATE POLICY tenant_isolation_policy_product ON eco_product FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));
-- Supplier RLS policy CREATE POLICY tenant_isolation_policy_supplier ON eco_supplier FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));
-- Importer RLS policy CREATE POLICY tenant_isolation_policy_importer ON eco_importer FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));
-- Installation RLS policy CREATE POLICY tenant_isolation_policy_installation ON installation FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));
-- EmissionRecord RLS policy CREATE POLICY tenant_isolation_policy_emission_record ON emission_record FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));
-- EmissionReport RLS policy CREATE POLICY tenant_isolation_policy_emission_report ON emission_report FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));
-- ComplianceReport RLS policy CREATE POLICY tenant_isolation_policy_compliance_report ON compliance_report FOR ALL USING (tenant_id = current_setting('app.current_tenant', true));

changeSet.rollback:
DROP POLICY IF EXISTS tenant_isolation_policy_product ON eco_product;
DROP POLICY IF EXISTS tenant_isolation_policy_supplier ON eco_supplier;
DROP POLICY IF EXISTS tenant_isolation_policy_importer ON eco_importer;
DROP POLICY IF EXISTS tenant_isolation_policy_installation ON installation;
DROP POLICY IF EXISTS tenant_isolation_policy_emission_record ON emission_record;
DROP POLICY IF EXISTS tenant_isolation_policy_emission_report ON emission_report;
DROP POLICY IF EXISTS tenant_isolation_policy_compliance_report ON compliance_report;

changeSet.sql:
-- Allow database owner to bypass RLS (for migrations and admin operations) -- This is optional but recommended for easier database administration
CREATE POLICY tenant_isolation_bypass_product ON eco_product FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);
CREATE POLICY tenant_isolation_bypass_supplier ON eco_supplier FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);
CREATE POLICY tenant_isolation_bypass_importer ON eco_importer FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);
CREATE POLICY tenant_isolation_bypass_installation ON installation FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);
CREATE POLICY tenant_isolation_bypass_emission_record ON emission_record FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);
CREATE POLICY tenant_isolation_bypass_emission_report ON emission_report FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);
CREATE POLICY tenant_isolation_bypass_compliance_report ON compliance_report FOR ALL TO CURRENT_USER USING (true) WITH CHECK (true);

changeSet.rollback:
DROP POLICY IF EXISTS tenant_isolation_bypass_product ON eco_product;
DROP POLICY IF EXISTS tenant_isolation_bypass_supplier ON eco_supplier;
DROP POLICY IF EXISTS tenant_isolation_bypass_importer ON eco_importer;
DROP POLICY IF EXISTS tenant_isolation_bypass_installation ON installation;
DROP POLICY IF EXISTS tenant_isolation_bypass_emission_record ON emission_record;
DROP POLICY IF EXISTS tenant_isolation_bypass_emission_report ON emission_report;
DROP POLICY IF EXISTS tenant_isolation_bypass_compliance_report ON compliance_report;
